import React from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import image from './images/react-logo.png'

const people = [
    'Alice',
    'Ainur',
    'Sofa',
    'Dima',
    'Alexa',
    'Anna',
    'Duglas',
    'Denis'
]

class App extends React.Component{

    constructor(props){
        super(props)
        this.textChange = this.textChange.bind(this)
        this.state = {
            search: ''
        }
    }

    textChange(input){
        this.setState({search: input.target.value})
    }

    render(){
        React.useEffect(() => {
            const result = people.filter(person => person.toLowerCase().includes(this.state.search))
        })
        return(
            <div>
                <Header image_src = {image}/>

                <div className='container'>
                    <input id='search' type='text' placeholder='Search' onChange={this.textChange}></input>
                </div>

                <div className='container'>
                    <h1>{this.state.search}</h1>
                    <ul>
                        {result.map(item => (
                            <li>{item}</li>
                        ))}
                    </ul>
                </div>

                <Footer/>
            </div>
        )
    }
}

export default App
