import React from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import image from './images/react-logo.png'

class App extends React.Component{

    constructor(props){
        super(props)
        this.textChange = this.textChange.bind(this)
        this.state = {
            text: ''
        }
    }

    textChange(input){
        this.setState({text: input.target.value})

        names = ['Alice', 'Julie', 'Anna', 'Dio']
        list_names = this.names.map(
        (n_name) => <li>{n_name}</li>
    )
    }

    

    render(){
        return(
            <div>
                <Header image_src = {image}/>

                <div className='container'>
                    <input id='search' type='text' placeholder='Search' onChange={this.textChange}></input>
                </div>

                <div className='container'>
                    <h1>{this.state.text}</h1>
                    <ul>
                        {this.list_names}
                    </ul>
                </div>

                <Footer/>
            </div>
        )
    }
}

export default App
